
public class Aula19ClasseMath {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//compara e retorna o maior numero de dois numeros
		System.out.println("O Maior número entre 9 e 2 é: " + Math.max(9,9));
		//retorna o menor entre dois numeros
		System.out.println("O Menor número entre 1 e 9 é: " + Math.min(1,9));
		
		System.out.println("A raiz quadrada de 81 é: "+Math.sqrt(81));

		System.out.println("O número aleatório gerado é: "+Math.random());
		
		//Round - Arredonda Numeros
		System.out.println("O Arredondamento de 7,8 é: "+Math.round(7.8));
		System.out.println("O Arredondamento de 7,4 é: "+Math.round(7.4));

	}

}
